# Get-AzLocation | Select-Object -Property "Location"# $size = Get-AzVMSize -Location "westeurope" | Where-Object { $_.NumberOfCores -eq 2 -and $_.MemoryInMB -eq 8GB/1MB } | Out-GridView -PassThru$location = "westeurope"$user = "rmoro"$pass = ConvertTo-SecureString "Admin1234567" -AsPlainText -Force$cred = New-Object System.Management.Automation.PSCredential($user, $pass)$size = Get-AzVMSize -Location "westeurope" | Where-Object { $_.NumberOfCores -eq 2 -and $_.MemoryInMB -eq 8GB/1MB } | Out-GridView -PassThru$rg = "CICE-Powershell"$vmName = "VM"$cantidadVM = 5[array]$vmJobList = $nullNew-AzResourceGroup -ResourceGroupName $rg -Location $locationfor($count = 1; $count -le $cantidadVM; $count++) {    $name = $vmName + $count    $vmJobList += New-AzVM -ResourceGroupName $rg `             -Location $location `             -Name $name `             -Size $size `             -Image "Win2019Datacenter" `             -Credential $cred `             -SecurityGroupName "NSG-$name" `
"todas las VMs creadas"
